// STYLED COMPONENTS //
const HeaderContainer = styled.header((props) => ({
    width:'100%',
    background:'#fff',
    height:60,
    borderBottom: ('1px solid ').concat(props.theme.border),
    display:'flex',
    justifyContent:'center',
    alignItems:'center',
    position:'relative',
    zIndex:7,
}));
const CenteredContent = styled.div((props) => ({
    width:'calc(100% - 30px)',
    maxWidth:1300,
    height:'100%',
    display:'flex',
    justifyContent:'space-between',
    alignItems:'center',
}));
const LeftContent = styled.div((props) => ({
    display:'flex',
    justifyContent:'flex-start',
    alignItems:'center',
    height:'100%',
}));
const RightContent = styled.div((props) => ({
    display:'flex',
    justifyContent:'flex-end',
    alignItems:'center',
}));
const RightLink = styled.div((props) => ({
    color: props.theme.darkText,
    fontSize:13,
    fontWeight:600,
    boxSizing:'border-box',
    display:'flex',
    alignItems:'center',
    marginRight:12,
    textTransform:'capitalize',
    justifyContent:'flex-start',
    cursor:'pointer',
    transition:'all .2s ease-in-out',
    ':hover':{
        color:props.theme.brand
    },
    '@media(max-width:600px)':{
        display:'none'
    }
}));
const Divider = styled.div((props) => ({
    height:24,
    width:1,
    background:'#DEDFEA',
    '@media(max-width:600px)':{
        display:'none'
    }
}));
const Advertise = styled.div((props) => ({
    color: props.theme.darkText,
    fontSize:13,
    fontWeight:600,
    boxSizing:'border-box',
    display:'flex',
    alignItems:'center',
    marginLeft:12,
    textTransform:'capitalize',
    justifyContent:'flex-start',
    cursor:'pointer',
    transition:'all .2s ease-in-out',
    ':hover':{
        color:props.theme.brand
    },
    '@media(max-width:1000px)':{
        display:'none',
    }
}));
const Logo = styled.svg((props) => ({
    height:23,
    '@media(max-width:600px)':{
        display:'none'
    }
}));
const Icon = styled.svg((props) => ({
    height:23,
    display:'none',
    fill:props.theme.brand,
    '@media(max-width:600px)':{
        display:'block'
    }
}));
const Hamburger = styled.svg((props) => ({
    height:18,
    marginLeft:12,
    fill: props.theme.darkText,
    display:'none',
    cursor:'pointer',
    transition:'fill .2s ease-in-out',
    ':hover':{
        fill: props.theme.brand
    },
    '@media(max-width:1000px)':{
        display:'flex',
    }
}));

// REDUX MAPPING //
const mapStateToProps = (state) => {
	return {
		theme: state.theme
	};
};

const mapDispatchToProps = (dispatch) => {
	return {
		
	};
};

export default connect(mapStateToProps,mapDispatchToProps)(Header);